/**
 * Register Block Pattern Category.
 */
function aino_register_pattern_categories() {

	register_block_pattern_category(
		'aino-basics',
		array( 'label' => __( 'Aino Basics', 'aino' ) )
	);
}

add_action( 'init', 'aino_register_pattern_categories' );

